mapper.update:
UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT *
FROM setmeal

    
        AND category_id = #{categoryId},
    
    
        AND name = #{name}
    
    
        AND status = #{status}
    

ORDER BY create_time DESC

mapper.delete:
DELETE FROM setmeal
WHERE id IN

    #{id}
